def makingAnagram(s1,s2):
    res = []
    toSearch = s2 if len(s1)<len(s2) else s1
    tempS2 = s1 if len(s1)<len(s2) else s2
    for i in range(len(s2)):
        finder = tempS2.find(toSearch[i])
        if finder==-1:
            res.append(toSearch[i])
        else:
            tempS2 = list(tempS2)
            tempS2[finder] = ""
            tempS2 = "".join(tempS2)
    return len(res)+len(tempS2)

s1 = ""
s2 = ""
print(makingAnagram(s1,s2))