from collections import Counter

def isValid(s):
    if len(s)<=1:return "YES"
    nbr = dict(Counter(s))
    ref = next(iter(nbr.values()))
    AllDif = [abs(nbr[i]) for i in nbr if nbr[i]!=ref]
    if len(AllDif)>1:
        counted = dict(Counter(AllDif))
        if len(counted)>1:return "NO"
        if abs(next(iter(nbr.values()))-ref)==1:return "YES"
        return "NO"
    else:return "YES"
print(isValid(""))